import React, { useState, useEffect } from "react";

const ListarNitems = () => {
  const [items, setItems] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState();
  const nomeC = document.getElementById("nitems").value;
  console.log(nomeC)
  useEffect(() => {
    fetch(`http://localhost:3001/findnameitems/${nomeC}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, [nomeC]);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>{<ReactLoading className="loading" height={400} width={300}/>}</div>;
  } else {
    return (
      //   <p>
      //     {items.map((item) => (
      //       <li id="caixas" key={item.id}>
      //         {"ID : " +
      //           item.Id +
      //           " - " +
      //           " Nome : " +
      //           item.Name +
      //           " Classe : " +
      //           item.Class +
      //           " Tipo de Dano : " +
      //           item.DamageType}
      //       </li>
      //     ))}
      //   </p>
      <p>
        {items.map((item) => (
          <li id="caixas" key={item.id}>
            <h3>
            {item.name}
            </h3>
            <br></br>
            <img
              src={`item/${item.item_id}.png`}
              alt={item.name}
              id="imgsitem"
            ></img>
            {" Description : " + item.explain}
            <br></br>
            {" Buy Price : " + item.buy_price}
            <br></br>
            {" Sell Price : " + item.sell_price}
          </li>
        ))}
      </p>
    );
  }
};

export default ListarNitems;
