import logo from "./logo.svg";
import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer";
import ContainerChamp from "./components/containerchamp";
import Nchamp from "./components/Nchamp";
import Nclass from "./components/Nclass";
import Nitems from "./components/Nitems";
import Nrunas from "./components/Nrunas";


import React, { useState } from "react";

function App() {
  const [champ, setChamp] = useState(false);

  const ListChamp = () =>{
    setChamp(true)
    setNchamp(false)
  }
  const [nchamp, setNchamp] = useState(false);

  const namechamp = () =>{
    setNchamp(true)
    setChamp(false)
  }
  const [nitems, setNitems] = useState(false);

  const nameitems = () =>{
    setNitems(true)
    setChamp(false)
    setNchamp(false)
    setNitems(false)
    setNclass(false)
  }
  const [nclass, setNclass] = useState(false);

  const nameclass = () =>{
    setNclass(true)
    setChamp(false)
    setNitems(false)
    setChamp(false)
    setNchamp(false)
    setNitems(false)
  }
  const [nrunas, setNrunas] = useState(false);

  const namerunas = () =>{
    setNrunas(true)
    setChamp(false)
  }

  return (
    <div className="App">
      <Header />
      <nav id="menu">
        <ul>
            <li><a href="#menu" onClick={ListChamp}>  Campeoes  </a></li>
            <li><a href="#menu">  Items  </a></li>
            <li><a href="#menu">  Runas  </a></li>
            <li><a href="#menu"> Buscar</a>
                <ul class = "drop">
                  <li><a href="#menu" onClick={namechamp}>Nome dos Campeoes</a></li>
                  <li><a href="#menu" onClick={nameclass}>Classe dos campeoes</a></li>
                  <li><a href="#menu" onClick={nameitems}>Nome dos Items</a></li>
                  <li><a href="#menu" onClick={namerunas}>Nome das Runas</a></li>
                </ul>
            </li>

        </ul>
        </nav>
          {
            champ && <ContainerChamp />
          }
          {
            nchamp && <Nchamp/>
          }

      <Footer />
    </div>
  );
}

export default App;
