import React, { useEffect, useState } from "react";
import FindNchamp from "./findnameChamp";
const Nchamp = () =>{

    const [execucao, setExecucao] = useState('')


    const listarchamps = () => {  
        let camp = document.getElementById("nchamp").value
        setExecucao(camp)
        console.log(camp)
    }
    useEffect(()=>{
        listarchamps();
    }, [execucao])




    return(

        <div id = "boxMenu">
            <div id = "title">Digite o nome do Campeao que deseja Buscar ! </div>
            <input type="text" id="nchamp" name="dados"></input>
            <button type="button" id="btNchamp" onClick={listarchamps}>
            Buscar 
            </button>

            {
                execucao && <FindNchamp />
            }


        </div>

    )
}

export default Nchamp;
