import logo from "./logo.svg";
import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer";
import ContainerChamp from "./components/containerchamp";
import ContainerItems from "./components/containeritems"
import Nchamp from "./components/Nchamp";
import Nclass from "./components/Nclass";
import Nitems from "./components/Nitems";
import Nrunas from "./components/Nrunas";


import React, { useState } from "react";

function App() {
  const [exec, setExec] = useState(false);

  const ListChamp = () =>{
      setExec(ContainerChamp)
  }

  const ListItem = () =>{
    setExec(ContainerItems)
  }



  const namechamp = () =>{
    setExec(Nchamp)
  }


  const nameitems = () =>{
    setExec(Nitems)

  }


  const nameclass = () =>{
    setExec(ContainerChamp)
  }


  const namerunas = () =>{
    setExec(ContainerChamp)
  }

  return (
    <div className="App">
      <Header />
      <nav id="menu">
        <ul>
            <li><a href="#menu" onClick={ListChamp}>  Campeoes  </a></li>
            <li><a href="#menu" onClick={ListItem}>  Items  </a></li>
            <li><a href="#menu">  Runas  </a></li>
            <li><a href="#menu"> Buscar</a>
                <ul class = "drop">
                  <li><a href="#menu" onClick={namechamp}>Nome dos Campeoes</a></li>
                  <li><a href="#menu" onClick={nameclass}>Classe dos campeoes</a></li>
                  <li><a href="#menu" onClick={nameitems}>Nome dos Items</a></li>
                  <li><a href="#menu" onClick={namerunas}>Nome das Runas</a></li>
                </ul>
            </li>

        </ul>
        </nav>
          {
            champ && <ContainerChamp />
          }
          {
            item && <ContainerItems />
          }
          {
            nchamp && <Nchamp/>
          }
          {
            nitems && <Nitems/>
          }
          {
            nclass && <Nclass/>
          }
          {
            nrunas && <Nrunas/>
          }

      <Footer />
    </div>
  );
}

export default App;
