import React, { useEffect, useState } from "react";
import Findclasschamp from "./findclassChamp"
const Nclass = () =>{

    const [exec, setExec] = useState('')
    const classitem = () =>{
        let camp = document.getElementById("nclass").value
        setExec(camp)
    }
    useEffect(()=>{
        classitem();
    }, [exec])
    return(
        <div id = "boxMenu">
            <div id = "title">Digite o nome da Classe que deseja Buscar ! </div>
            <input type="text" id="nclass" name="dados" placeholder="Campeao"></input>
            <button type="button" id="btNclass" onClick={classitem}>Buscar</button>

            {
                exec && <Findclasschamp/>
            }

        </div>
    )
}

export default Nclass;
