import React, { useState, useEffect } from "react";
import ReactLoading from 'react-loading';
const Oneitems = (props) =>{

    const [itemss, setitemss] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState();
    console.log(props.iditems)

  useEffect(() => {
    fetch(`http://localhost:3001/findiditemss/${props.iditems}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setitemss(result);
          setIsLoaded(true);
        },
        (error) => {
          setError(error);
          setIsLoaded(true);
        }
      );
  },[props.iditems]);

  const renderSuperitemss = (items) =>{
    console.log(items)
    if(items.upper_items.length){
    return JSON.parse(items.upper_items[0].replace(/'/g, '"')).map((superitems)=>{

      return <img class = "itemssb" src={`items/${superitems}.png`} alt = "itemssb"></img>

    })
   }
   return <p>Sem itemss Superiores</p>
  }

  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div className="load">{<ReactLoading className= "loading" color={'#EDB852'}type={'spinningBubbles'} height={350} width={250}/>}</div>;
  } else {
    console.log(itemss)
    return (
        <div>

          <li id="boxitems" key={items.id}>

              <div className = "cont1" >

            <h3 id="titlechamp">{items.name}</h3>

            <img
              src={`items/${items.items_id}.png`}
              alt={items.name}
              id="itemsimage"
            ></img>
            </div>
            <div className = "conteudo2">

              <div className = "txtexp">{items.explain}</div>
            </div>
            <div className = "pai">
            <div className = "txtsup">itemss Superiores </div>
            <div id ="itemssup">
              {renderSuperitemss(items)}
              {/* <img class = "itemssb" src={`items/${JSON.parse(items.upper_items.pop().replace(/'/g, '"'))[0]}.png`} alt = "itemssb"></img> */}
              {/* <img class = "itemssb" src={`items/${items.upper_items[1]}.png`} alt = "itemssb"></img> */}
            </div>
            <div id ="prices">
              <div className="txtitems"><img className="icons" src="compra.png" alt=""></img>Preco de Compra : {items.buy_price}</div>

              <div className="txtitems"><img className="icons" src="compra.png" alt=""></img>Preco de Venda : {items.sell_price}</div>

              <div className="txtitems"> Tipo do items : {items.tag}</div>
            </div>
            </div>





          </li>
        )
      </div>
    );

  }





}

export default Oneitems;
