import React, { useState, useEffect } from "react";

const Oneitem = (props) =>{

    const [champions, setItems] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState();
    console.log(props.nomeItem)

  useEffect(() => {
    fetch(`http://localhost:3001/findnameitems/${props.nomeItem}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setChampions(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
        <div>
        {champions.map((item) => (
          <li id="box" key={item.id} style={{backgroundImage: (`URL(splash/${item.id}_0.jpg)`)}}>
              <div class = "cont1" >

            <h3 id="titlechamp">{item.name}</h3>

            <img
              src={`loading/${item.id}_0.jpg`}
              alt={item.id}
              id="champimage"
            ></img>
            </div>
            <div class = "cont2" >
                <h2 id="subtitle">{item.title}</h2>
                <p id="champclass">
                    <p id="tip"> Classe : </p> <p id="resclass">{item.tags[0]} </p>
                </p>
                <br></br>
                <p id= "tip">
                    SKILLS
                </p>
                <br></br>
                {
                    <img
                    src={`spell/${item.id}E.png`}
                    alt={item.id}
                    id="skillimg"
                  ></img>
                }
                {
                    <img
                    src={`spell/${item.id}Q.png`}
                    alt={item.id}
                    id="skillimg"
                  ></img>
                }
                {
                    <img
                    src={`spell/${item.id}W.png`}
                    alt={item.id}
                    id="skillimg"
                  ></img>
                }
                {
                    <img
                    src={`spell/${item.id}R.png`}
                    alt={item.id}
                    id="skillimg"
                  ></img>
                }
            <div class = "desc">
                <p id="champdesc">
                    {item.blurb}
                </p>

            </div>

            <div class = "ct1"></div>  

            </div>


          </li>
        ))}
      </div>
    );
  }





}

export default Oneitem;
