import React, { useState, useEffect } from "react";
import ReactLoading from 'react-loading';
const ListarNclass = () => {
  const [champions, setChampions] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState();
  const nomeC = document.getElementById("nclass").value;
  console.log(nomeC);

  useEffect(() => {
    fetch(`http://localhost:3001/findclass/${nomeC}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setChampions(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, [nomeC]);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div className="load">{<ReactLoading className= "loading" type={'spin'} height={400} width={300}/>}</div>;
  } else {
    return (
      <p>
        {champions.map((item) => (
          <li id="caixas" key={item.id}>
            <h3>{item.Name}</h3>
            <br></br>
            <img
              src={`assets/${item.Name}_0.jpg`}
              alt={item.Name}
              id="imgs"
            ></img>
            {" Classe : " + item.Class}
            <br></br>
            {" Tipo de Dano : " + item.DamageType}
          </li>
        ))}
      </p>
    );
  }
};

export default ListarNclass;
