import React, { useState, useEffect } from "react";
import ReactLoading from 'react-loading';
const Nitem = (props) =>{

    const [items, setItems] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState();
    let nomeC = document.getElementById("nitems").value;

  useEffect(() => {
    fetch(`http://localhost:3001/findnameitems/${props.idItem}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  },[props.idItem]);

  const renderSuperItems = (item) =>{
    console.log(item)
    if(item.upper_item.length){
    return JSON.parse(item.upper_item[0].replace(/'/g, '"')).map((superitem)=>{

      return <img class = "itemsb" src={`item/${superitem}.png`} alt = "itemsb"></img>

    })
   }
   return <p>Sem items Superiores</p>
  }

  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div className="load">{<ReactLoading className= "loading" type={'spin'} height={350} width={250}/>}</div>;
  } else {
    console.log(items)
    return (
        <div>
        {items.map((item) => (
          <li id="boxitem" key={item.id}>

              <div className = "cont1" >

            <h3 id="titlechamp">{item.name}</h3>

            <img
              src={`item/${item.item_id}.png`}
              alt={item.name}
              id="itemimage"
            ></img>
            </div>
            <div className = "conteudo2">
              {item.explain}
            </div>
            <div id ="itemsup"> Items Superiores :
              {renderSuperItems(item)}
              {/* <img class = "itemsb" src={`item/${JSON.parse(item.upper_item.pop().replace(/'/g, '"'))[0]}.png`} alt = "itemsb"></img> */}
              {/* <img class = "itemsb" src={`item/${item.upper_item[1]}.png`} alt = "itemsb"></img> */}
            </div>
            <div id ="prices">
              <p>Preco de Compra : {item.buy_price}</p>
              <br></br>
              <p>Preco de Venda : {item.sell_price}</p>
              <br></br>
              <p> Tipo do Item : {item.tag}</p>
            </div>




          </li>
        ))}
      </div>
    );

  }





}

export default Nitem;
