import logo from "./logo.svg";
import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer";

import ContainerItems from "./components/containeritems"
import Nchamp from "./components/Nchamp";
import Nclass from "./components/Nclass";
import Nitems from "./components/Nitems";
import Nrunas from "./components/Nrunas";
import FindChamp from "./components/findChamp";


import React, { useState } from "react";

function App() {
  const [exec, setExec] = useState('d');
  const [exec2, setExec2] = useState('');

  const [boneco, setBoneco] = useState("")

  const ListChamp = () =>{
      setExec('')
  }

  const ListItem = () =>{
    setExec2('item')
  }



  const namechamp = () =>{
    setExec2('nchamp')
  }


  const nameitems = () =>{
    setExec2('nitems')

  }


  const nameclass = () =>{
    setExec2('nclass')
  }


  const namerunas = () =>{
    setExec2('nrunas')
  }

  return (
    <div className="App">
      <Header />
      <nav id="menu">
        <ul>
            <li><a href="#menu" onClick={ListChamp}>  Campeoes  </a></li>
            <li><a href="#menu" onClick={ListItem}>  Items  </a></li>
            <li><a href="#menu">  Runas  </a></li>
            <li><a href="#menu"> Buscar</a>
                <ul class = "drop">
                  <li><a href="#menu" onClick={namechamp}>Nome dos Campeoes</a></li>
                  <li><a href="#menu" onClick={nameclass}>Classe dos campeoes</a></li>
                  <li><a href="#menu" onClick={nameitems}>Nome dos Items</a></li>
                  <li><a href="#menu" onClick={namerunas}>Nome das Runas</a></li>
                </ul>
            </li>

        </ul>
        </nav>
          {
            exec === '' && <FindChamp trocaExec={setExec} rota={exec}/>
          }

          {
            exec2 === 'item' && <ContainerItems />
          }
          {
            exec2 === 'nchamp' && <Nchamp/>
          }
          {
            exec === 'nitems' && <Nitems/>
          }
          {
            exec === 'nclass' && <Nclass/>
          }
          {
            exec === 'nrunas' && <Nrunas/>
          }

      <Footer />
    </div>
  );
}

export default App;
