import React, { useState, useEffect } from "react";

const ListarChampions = () => {
  const [champions, setChampions] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState();

  useEffect(() => {
    fetch(`http://localhost:3001/findall`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setChampions(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    console.log(champions)

    const [execucao, setExecucao] = useState(false)
    const spec = () =>{
        setExecucao(true)
    }
    useEffect(()=>{

    })

    return (

      <p>
        {champions.map((item) => (
          <li id="caixas" key={item.id} onClick={spec}>
            <h3>
            {item.Name}
            </h3>
            <br></br>
            <img
              src={`assets/${item.Name}_0.jpg`}
              alt={item.Name}
              id="imgs"
            ></img>
            {" Classe : " + item.Class}
            <br></br>
            {" Tipo de Dano : " + item.DamageType}
          </li>
        ))}

      </p>
    );
  }
};

export default ListarChampions;
