import React, { useState, useEffect } from "react";

const ListarItems = (props) => {
  const [items, setItems] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState();
  const load = () =>{

    <ReactLoading type={String} height={'20%'} width={'20%'} />

  }
  useEffect(() => {
    fetch(`http://localhost:3001/findallitems`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div>
        {items.map((item) => (
          <li id="caixas" key={item.id} onClick={() => (props.trocaExecit(item.item_id))}>
            <h3>
            {item.name}
            </h3>
            <br></br>
            <img
              src={`item/${item.item_id}.png`}
              alt={item.name}
              id="imgsitem"
            ></img>

          </li>
        ))}
      </div>
    );
  }
};

export default ListarItems;
