import React, { useState, useEffect } from "react";

const Onechamp = (props) =>{

    const [champions, setChampions] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState();
    console.log(props.nomeChamp)

  useEffect(() => {
    fetch(`http://localhost:3001/findname/${props.nomeChamp}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setChampions(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <p>
        {champions.map((item) => (
          <li id="box" key={item.id} style={{backgroundImage: (`URL(splash/${item.id}_0.jpg)`)}}>
              <div class = "cont1" >

            <h3 id="titlechamp">{item.name}</h3>

            <img
              src={`loading/${item.id}_0.jpg`}
              alt={item.id}
              id="champimage"
            ></img>
            </div>
            <div class = "cont2" >
                <h2 id="subtitle">{item.title}</h2>
                <p id="champclass">
                    <p id="tip"> Classe : </p> <p id="resclass">{item.tags[0,1]} </p>
                </p>


            <div class = "ct1"></div>  

            </div>


          </li>
        ))}
      </p>
    );
  }





}

export default Onechamp;
