import React, { useState, useEffect } from "react";
import ReactLoading from 'react-loading';
const ListarNchampions = (props) => {
  const [champions, setChampions] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState();
    const nomeC = document.getElementById("nchamp").value;
    const trocarL = (title) =>{
      const split = title.split('');
      split[0] = split[0].toUpperCase();
      const split2 = split.join('');
      return split2;
    }
  useEffect(() => {
    fetch(`http://localhost:3001/findname/${nomeC}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setChampions(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, [nomeC]);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div className="load">{<ReactLoading className= "loading" color={'#EDB852'}type={'spinningBubbles'} height={350} width={250}/>}</div>;
  } else {
    const mostrar = champions.map((item) => (

      <li id="box" key={item.id} style={{backgroundImage: (`URL(splash/${item.id}_0.jpg)`)}}>
          <div class = "cont1" >

        <h3 id="titlechamp">{item.name}</h3>
        <img
          src={`loading/${item.id}_0.jpg`}
          alt={item.id}
          id="champimage"
        ></img>
        </div>
        <div class = "cont2" >
            <div class= "headerchamp">
            <div id="subtitle">{trocarL(item.title)}</div>
            <div id="tip"> Classe : </div> <div id="resclass">{item.tags[0]} </div>
            </div>
            <br></br>
            <div id= "skills">
                SKILLS
            </div>
            <br></br>
            {
                <img
                src={`spell/${item.id}E.png`}
                alt={item.id}
                id="skillimg"
              ></img>
            }
            {
                <img
                src={`spell/${item.id}Q.png`}
                alt={item.id}
                id="skillimg"
              ></img>
            }
            {
                <img
                src={`spell/${item.id}W.png`}
                alt={item.id}
                id="skillimg"
              ></img>
            }
            {
                <img
                src={`spell/${item.id}R.png`}
                alt={item.id}
                id="skillimg"
              ></img>
            }
        <div class = "desc">
            <p id="champdesc">
                {item.blurb}
            </p>

        </div>

        <div class = "ct1"></div>  

        </div>


      </li>
    ))


    return (
        <div>

      </div>
    );
  }



};

export default ListarNchampions;
