import React, { useState, useEffect } from "react";

const ListarNchampions = () => {
  const [champions, setChampions] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState();
  const nomeC = document.getElementById("nchamp").value;
  console.log(nomeC)
  useEffect(() => {
    fetch(`http://localhost:3001/findname/${nomeC}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setChampions(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (

      <p>
        {/* {champions.map((item) => (
          <li id="caixas" key={item.id}>
            <h3>
            {item.Name}
            </h3>
            <br></br>
            <img
              src={`assets/${item.Name}_0.jpg`}
              alt={item.Name}
              id="imgs"
            ></img>
            {" Classe : " + item.Class}
            <br></br>
            {" Tipo de Dano : " + item.DamageType}
          </li>
        ))} */}

      </p>
    );
  }
};

export default ListarNchampions;
