import React, { useState, useEffect } from "react";

const Oneitem = (props) =>{

    const [items, setItems] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState();
    console.log(props.idItem)

  useEffect(() => {
    fetch(`http://localhost:3001/findnameitems/${props.idItem}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  },[props.idItem]);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
        <div>
        {items.map((item) => (
          <li id="boxitem" key={item.id}>
              <div class = "cont1" >

            <h3 id="titlechamp">{item.name}</h3>

            <img
              src={`item/${item.item_id}.png`}
              alt={item.name}
              id="itemimage"
            ></img>
            <div>Items necessarios para a construcao :
              <img class = "itemsb" src={`item/${item.upper_item[0]}.png`}></img>
            </div>
            </div>



          </li>
        ))}
      </div>
    );
  }





}

export default Oneitem;
