import FindnItems from "./findnameitems"
const Nitems = () =>{


        const [exec, setExec] = useState('')

        const Sitems = () =>{
            let camp = document.getElementById("nitems").value;
            setExec(camp)
        }
        useEffect(()=>{
            Sitems();
        }, [exec])
    return(
        <div id = "boxMenu">
            <div id = "title">Digite o nome do Item que deseja Buscar ! </div>
            <input type="text" id="nitems" name="dados" placeholder="Campeao"></input>
            <button type="button" id="btNitems" onClick={Sitems}>Buscar</button>
            {
                exec && <FindnItems/>
            }

        </div>
    )
}

export default Nitems;
