import React, { useState, useEffect } from "react";

const ListarItems = () => {
  const [items, setItems] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState();

  useEffect(() => {
    fetch(`http://localhost:3001/findnameitems`)
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);
  if (error) {
    return <div>Error : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      //   <p>
      //     {items.map((item) => (
      //       <li id="caixas" key={item.id}>
      //         {"ID : " +
      //           item.Id +
      //           " - " +
      //           " Nome : " +
      //           item.Name +
      //           " Classe : " +
      //           item.Class +
      //           " Tipo de Dano : " +
      //           item.DamageType}
      //       </li>
      //     ))}
      //   </p>
      <p>
        {items.map((item) => (
          <li id="caixas" key={item.id}>
            <h3>
            {item.name}
            </h3>
            <br></br>
            <img
              src={`item/${item.item_id}.png`}
              alt={item.name}
              id="imgsitem"
            ></img>
            {" Description : " + item.explain}
            <br></br>
            {" Buy Price : " + item.buy_price}
            <br></br>
            {" Sell Price : " + item.sell_price}
          </li>
        ))}
      </p>
    );
  }
};

export default ListarItems;
