import logo from "./logo.svg";
import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer";
import ContainerChamp from "./components/containerchamp";
import Search from "./components/search";

import React, { useState } from "react";

function App() {
  const [champ, setChamp] = useState(false);

  const ListChamp = () =>{
    setChamp(true)
    setSearch(false)
  }
  const [search, setSearch] = useState(false);

  const busca = () =>{
    setSearch(true)
    setChamp(false)
  }

  return (
    <div className="App">
      <Header />
      <ul id="menu">

            <li><a href="#menu" onClick={ListChamp}>  Campeoes  </a></li>
            <li><a href="#menu">  Items  </a></li>
            <li><a href="#menu">  Runas  </a></li>
            <li><a href="#menu" onClick={busca}> Buscar</a>
                <ul>
                  <li><a href="#menu">Nome dos Campeoes</a></li>
                  <li><a href="#menu">Classe dos campeoes</a></li>
                  <li><a href="#menu">Nome dos Items</a></li>
                  <li><a href="#menu">Nome das Runas</a></li>
                </ul>
            </li>


        </ul>
          {
            champ && <ContainerChamp />
          }
          {
            search && <Search/>
          }

      <Footer />
    </div>
  );
}

export default App;
